        #region Deep Save
		
        public void DeepSave()
        {
            Save();
            
                if (colSysProvincia != null)
                {
                    colSysProvincia.SaveAll();
               }
		
                if (colSysParentescoRecords != null)
                {
                    colSysParentescoRecords.SaveAll();
               }
		
                if (colSysPacienteRecords != null)
                {
                    colSysPacienteRecords.SaveAll();
               }
		}
